const FoodList = ({ foods }) => {
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});
    const [navScrolled, setNavScrolled] = useState(false);
    const [headerHeight, setHeaderHeight] = useState(0);
    const [navHeight, setNavHeight] = useState(0); // Để theo dõi chiều cao của nav

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        if (foods.length > 0) {
            const grouped = foods.reduce((acc, food) => {
                const type = food.typeFood; 
                if (!acc[type]) {
                    acc[type] = [];
                }
                acc[type].push(food);
                return acc;
            }, {});
            setGroupedFoods(grouped);
        }
    }, [foods]);

    // Handle scroll event and calculate header and nav height to adjust nav position
    useEffect(() => {
        const headerElement = document.querySelector("nav");
        if (headerElement) {
            setHeaderHeight(headerElement.offsetHeight);
            setNavHeight(headerElement.offsetHeight); // Gán chiều cao của nav
        }
        
        const handleScroll = () => {
            setNavScrolled(window.scrollY > headerHeight);
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, [headerHeight]);

    const scrollToSection = (type) => {
        const element = document.getElementById(type);
        if (element) {
            const offsetPosition = element.getBoundingClientRect().top + window.scrollY - (headerHeight + navHeight); // Thêm chiều cao của nav
            
            window.scrollTo({
                top: offsetPosition,
                behavior: "smooth" // Cuộn mượt mà
            });
        }
    };

    return (
        <div className="px-12">
            <nav className={`transition-all duration-300 z-10 ${navScrolled ? `fixed bg-black text-white shadow-lg` : 'relative bg-transparent text-black'}`}
                 style={{ top: navScrolled ? `${headerHeight}px` : '0' }}>
                <ul className="flex space-x-4 p-4">
                    {Object.keys(groupedFoods).map((type) => (
                        <li key={type}>
                            <a 
                                href={`#${type}`} 
                                className="hover:underline cursor-pointer"
                                onClick={(e) => {
                                    e.preventDefault(); // Ngăn chặn hành động mặc định
                                    scrollToSection(type); // Gọi hàm cuộn đến phần tử
                                }}
                            >
                                {type}
                            </a>
                        </li>
                    ))}
                </ul>
            </nav>

            {Object.entries(groupedFoods).map(([type, foods]) => (
                <div key={type} className="my-4" id={type}>
                    <h5 className="mb-3 flex items-center justify-between cursor-pointer" onClick={() => toggleFoodList(type)}>
                        <span className="text-lg font-semibold">{type}</span>
                        <div className="border border-dashed h-px grow mx-2"></div>
                        {isOpen[type] ? '▲' : '▼'}
                    </h5>
                    {/* Food items under each category */}
                    <div className={`transition-all duration-300 ${isOpen[type] ? '' : 'hidden'}`}>
                        <div className="grid grid-cols-3 gap-4">
                            {foods.map((food) => (
                                <div key={food._id} className="text-center border p-4 rounded-md shadow-sm" style={{ width: "160px", height: "250px" }}>
                                    {food.image && (
                                        <img
                                            src={food.image}
                                            className="w-full h-32 object-cover mb-2"
                                            style={{ borderRadius: "8px" }}  
                                            alt={food.name}
                                        />
                                    )}
                                    <p className="font-semibold">{food.name}</p>
                                    <p className="text-yellow-500 font-bold">{food.price} VND</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default FoodList;
