import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FoodManager = () => {
    const [foods, setFoods] = useState([]);
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState('');
    const [editingFood, setEditingFood] = useState(null);
    const [useInputForType, setUseInputForType] = useState(false);
    // Predefined food types for the selector
    const predefinedTypes = ['Drink', 'Snack', 'Main Course', 'Dessert'];

    // Fetch all food items
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    // Create a new food item
    const createFood = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood); // Append typeFood to formData
        if (image) {
            formData.append('image', image);
        }

        try {
            await axios.post('http://localhost:5000/api/foods', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            fetchFoods(); // Refresh the list
            setName('');
            setPrice('');
            setTypeFood('');
            setImage(null);
        } catch (error) {
            console.error('Error creating food:', error);
        }
    };

    // Handle edit
    const handleEdit = (food) => {
        setEditingFood(food);
        setName(food.name);
        setPrice(food.price);
        setTypeFood(food.typeFood);
    };

    // Update a food item
    const updateFood = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood); // Append typeFood to formData
        if (image) {
            formData.append('image', image);
        }

        try {
            await axios.put(`http://localhost:5000/api/foods/${editingFood._id}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            fetchFoods(); // Refresh the list
            setEditingFood(null);
            setName('');
            setPrice('');
            setTypeFood('');
            setImage(null);
        } catch (error) {
            console.error('Error updating food:', error);
        }
    };

    // Delete a food item
    const deleteFood = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/foods/${id}`);
            fetchFoods(); // Refresh the list
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file); // Set image if file type is correct
        }
    };

    useEffect(() => {
        fetchFoods(); // Fetch foods when component mounts
    }, []);

    return (
        <div>
            <h1>Food Management</h1>
            <form onSubmit={editingFood ? updateFood : createFood}>
                <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required/>
                <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required/>
                {!useInputForType ? (
                    <select value={typeFood} onChange={(e) => setTypeFood(e.target.value)} required>
                        <option value="">Select Food Type</option>
                        {predefinedTypes.map((type) => (
                            <option key={type} value={type}>
                                {type}
                            </option>
                        ))}
                    </select>
                ) : (
                    <input
                        type="text"
                        placeholder="Enter Food Type"
                        value={typeFood}
                        onChange={(e) => setTypeFood(e.target.value)}
                        required
                    />
                )}

                {/* Button to toggle between selector and input */}
                <button
                    type="button"
                    onClick={() => setUseInputForType(!useInputForType)}
                >
                    {useInputForType ? 'Use Selector' : 'Enter Custom Type'}
                </button>

                <input type="file" onChange={handleFileChange} />
                <button type="submit">{editingFood ? 'Update Food' : 'Add Food'}</button>
            </form>
            <ul>
                {foods.map((food) => (
                    <li key={food._id}>
                        <h2>{food.name}</h2>
                        <p>Price: {food.price}</p>
                        <p>Type: {food.typeFood}</p>
                        {food.image && <img src={food.image} alt={food.name} width="100" />}
                        <button onClick={() => handleEdit(food)}>Edit</button>
                        <button onClick={() => deleteFood(food._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FoodManager;
