import { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

const Home = () => {
    const [foods, setFoods] = useState([]);

    // Fetch all food items
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div class="mt-4">
            <div class="row">
                <div class="col-sm-6 col-lg-4">
                    {foods.map((food) => (
                        <li key={food._id}>

                            <div class="card card-couser-item">
                                {food.image && <img src={food.image} class="card-img-top" alt={food.name} width="100" />}
                                <div class="card-body">
                                    <h5 class="card-title underline-none">{food.name}</h5>
                                    <p class="description card-text">{food.price}</p>
                                    <Link href="/Foods/manager" class="btn btn-primary">Đăng ký</Link>
                                </div>
                            </div>
                        </li>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Home;
