import { useEffect, useState } from "react";

const FoodList = ({ foods }) => {
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        if (foods.length > 0) {
            const grouped = foods.reduce((acc, food) => {
                const type = food.typeFood; // Assuming each food has a typeFood property
                if (!acc[type]) {
                    acc[type] = [];
                }
                acc[type].push(food);
                return acc;
            }, {});
            setGroupedFoods(grouped);
        }
    }, [foods]);

    return (
        <div className="px-12">
            <nav className="mb-4 ms-auto">
                <ul className="flex space-x-4">
                    {Object.keys(groupedFoods).map((type) => (
                        <li key={type}>
                            <a href={`#${type}`} className="text-blue-500 hover:underline cursor-pointer">
                                {type}
                            </a>
                        </li>
                    ))}
                </ul>
            </nav>

            {Object.entries(groupedFoods).map(([type, foods]) => (
                <div key={type} className="my-4" id={type}>
                    {/* Category header with toggle functionality */}
                    <h5 className="mb-3 flex items-center justify-between cursor-pointer" onClick={() => toggleFoodList(type)}>
                        <span className="text-lg font-semibold">{type}</span>
                        <div className="border border-dashed h-px grow mx-2"></div>
                        {isOpen[type] ? '▲' : '▼'}
                    </h5>

                    <div className={`transition-all duration-300 ${isOpen[type] ? '' : 'hidden'}`}>
                        <div className="grid grid-cols-3 gap-4">
                            {foods.map((food) => (
                                <div key={food._id} className="text-center border p-4 rounded-md shadow-sm">
                                    {food.image && (
                                        <img src={food.image} className="w-40 h-120 object-cover mb-2" alt={food.name} />
                                    )}
                                    <p className="font-semibold">{food.name}</p>
                                    <p className="text-yellow-500 font-bold">{food.price} VND</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default FoodList;
