import { useEffect, useState } from "react";

const FoodList = ({ foods }) => {
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});

    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Group food items by typeFood using the passed 'foods' prop
    useEffect(() => {
        if (foods.length > 0) {
            const grouped = foods.reduce((acc, food) => {
                const type = food.typeFood; // Assuming each food has a typeFood property
                if (!acc[type]) {
                    acc[type] = [];
                }
                acc[type].push(food);
                return acc;
            }, {});
            setGroupedFoods(grouped);
        }
    }, [foods]);

    return (
        <div className="">
            {Object.entries(groupedFoods).map(([type, foods]) => (
                <div key={type} className="my-4">
                    <h5 className="mb-3 flex items-center justify-between cursor-pointer" onClick={() => toggleFoodList(type)}>
                        {type}
                        <div className="border border-dashed h-px grow dark:border-white-12 border-white-016 mx-2"></div>
                        {isOpen[type] ? '▲' : '▼'}
                    </h5>
                    <div className={`transition-all duration-300 ${isOpen[type] ? '' : 'hidden'}`}>
                        <div className="row">
                            {foods.map((food) => (
                                <div key={food._id} className="col-sm-6 col-lg-4 mb-3">
                                    <div className="card" style={{ width: "18rem" }}>
                                        {food.image && (
                                            <img src={food.image} className="card-img-top" alt={food.name} />
                                        )}
                                        <div className="card-body text-uppercase text-center">
                                            <p className="card-text">{food.name}</p>
                                            <p className="text-warning font-weight-bold">
                                                {food.price} VND
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default FoodList;
