import { useEffect, useState } from "react";
import axios from 'axios';

const Home = () => {
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({}); // Tracks the open state of each group

    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            // Group food items by typeFood
            const grouped = response.data.reduce((acc, food) => {
                const type = food.typeFood; // Assuming each food has a typeFood property
                if (!acc[type]) {
                    acc[type] = [];
                }
                acc[type].push(food);
                return acc;
            }, {});
            setGroupedFoods(grouped);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div className="container">
            {Object.entries(groupedFoods).map(([type, foods]) => (
                <div key={type} className="my-4">
                    <h5 className="mb-3 d-flex justify-content-between align-items-center" onClick={() => toggleFoodList(type)} style={{ cursor: 'pointer' }}
                    >
                        {type} {isOpen[type] ? '▲' : '▼'}
                    </h5>
                    <div className={`${isOpen[type] ? '' : 'collapse'}`}>
                        <div className="row">
                            {foods.map((food) => (
                                <div key={food._id} className="col-sm-6 col-lg-4 mb-3">
                                    <div className="card text-center border-0">
                                        {food.image && (
                                            <img
                                                src={food.image}
                                                className="card-img-top"
                                                alt={food.name}
                                                style={{ height: "150px", objectFit: "cover" }}
                                            />
                                        )}
                                        <div className="card-body p-2">
                                            <h6 className="card-title text-uppercase">
                                                {food.name}
                                            </h6>
                                            <p className="text-warning font-weight-bold">
                                                {food.price} VND
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Home;
