import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import isEqual from 'lodash/isEqual';
import "./FoodManagerForm.css"
import axios from 'axios';

const FoodManagerForm = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const foodData = location.state || {};
    const [name, setName] = useState(foodData.name || '');
    const [price, setPrice] = useState(foodData.price || '');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState(foodData.typeFood || '');
    const [typeFoodList, setTypeFoodList] = useState([]);
    const [isCustomType, setIsCustomType] = useState(false); // State to toggle between select and input

    // Fetch all food items
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            console.log(response.data.typeFood)
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) {
            formData.append('image', image);
        }
        const url = isEqual(foodData, {}) ? 'http://localhost:5000/api/foods' : `http://localhost:5000/api/foods/${foodData._id}`;
        const method = isEqual(foodData, {}) ? 'post' : 'put';

        try {
            await axios({
                method,
                url,
                data: formData,
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            navigate('/Foods/manager');
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    return (
        <div className='items-center'>
            <h1 className='Formtitle'> {isEqual(foodData, {}) ? 'Thêm món ăn' : 'Cập nhập'}</h1>
            <div className="Formbody flex">
                <form className="w-full max-w-sm " onSubmit={handleSubmit}>
                    <div className="md:flex md:items-center mb-6">
                        <div className="md:w-1/3">
                            <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="NameInput">
                                Tên món ăn
                            </label>
                        </div>
                        <div className="md:w-2/3">
                            <input className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                id="NameInput"
                                type="text"
                                placeholder="Name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                            />
                        </div>
                    </div>
                    <div className="md:flex md:items-center mb-6">
                        <div className="md:w-1/3">
                            <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="PriceInput">
                                Giá
                            </label>
                        </div>
                        <div className="md:w-2/3">
                            <input className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                id="PriceInput"
                                type="number"
                                placeholder="Price"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                                required
                            />
                        </div>
                    </div>
                    <div className="md:flex md:items-center mb-6">
                        <div className="md:w-1/3">
                            <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="typeFood">
                                Loại
                            </label>
                        </div>
                        <div className="md:w-2/3">
                            {isCustomType ? (
                                <input
                                    className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                    type="text"
                                    placeholder="Custom TypeFood"
                                    value={typeFood}
                                    onChange={(e) => setTypeFood(e.target.value)}
                                    required
                                />
                            ) : (
                                <>
                                    <select
                                        className="bg-gray-200 border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                        value={typeFood}
                                        onChange={(e) => setTypeFood(e.target.value)}
                                        required
                                    >
                                        <option value="">Select Type Food</option>
                                        {typeFoodList.map((type, index) => (
                                            <option key={index} value={type}>{type}</option>
                                        ))}
                                    </select>
                                    <button
                                        type="button"
                                        className="ml-2 shadow bg-purple-500 hover:bg-purple-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded"
                                        onClick={() => setIsCustomType(true)}
                                    >
                                        Custom
                                    </button>
                                </>
                            )}
                        </div>
                    </div>
                    <div className="md:flex md:items-center mb-6">
                        <div className="md:w-1/3">
                            <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="file_input">
                                Upload file
                            </label>
                        </div>
                        <div className="md:w-2/3">
                            <input className="block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 focus:outline-none"
                                id="file_input"
                                type="file"
                                onChange={handleFileChange}
                            />
                        </div>
                    </div>
                    <div className="md:flex md:items-center">
                        <div className="md:w-1/3"></div>
                        <div className="md:w-2/3">
                            <button className="shadow bg-purple-500 hover:bg-purple-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded" type="submit">
                                {isEqual(foodData, {}) ? 'Thêm món' : 'Cập nhập'}
                            </button>
                        </div>
                    </div>
                </form>
                <div className='m-4'>
                    {foodData.image && (
                        <div className="mb-4 text-center">
                            <p>Current Image</p>
                            <img
                                src={foodData.image}
                                alt={foodData.name}
                                className="w-32 h-32 object-cover"
                            />
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default FoodManagerForm;
