import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './FoodManager.css';

const FoodManager = () => {
    const [foods, setFoods] = useState([]);
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isWarningOpen, setIsWarningOpen] = useState(false);
    const [foodToDelete, setFoodToDelete] = useState(null);
    const navigate = useNavigate();

    // Fetch all food items
    const fetchFoods = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
        } catch (error) {
            setError('Error fetching foods. Please try again.');
            console.error('Error fetching foods:', error);
        } finally {
            setLoading(false);
        }
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        const grouped = foods.reduce((acc, food) => {
            const type = food.typeFood || 'Others';
            if (!acc[type]) {
                acc[type] = [];
            }
            acc[type].push(food);
            return acc;
        }, {});
        setGroupedFoods(grouped);
    }, [foods]);

    // Delete a food item with confirmation
    const confirmDeleteFood = async () => {
        if (foodToDelete) {
            setLoading(true);
            setError(null);
            try {
                await axios.delete(`http://localhost:5000/api/foods/${foodToDelete}`);
                fetchFoods();
                setIsWarningOpen(false); // Close the warning after deletion
            } catch (error) {
                setError('Error deleting food. Please try again.');
                console.error('Error deleting food:', error);
            } finally {
                setLoading(false);
            }
        }
    };

    // Show warning dialog
    const handleDeleteClick = (id) => {
        setFoodToDelete(id);
        setIsWarningOpen(true);
    };

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    useEffect(() => {
        fetchFoods(); // Fetch foods on component mount
    }, []);

    return (
        <div>
            <div className='FoodListBG'>
                <div className="px-12">
                    <h1 className="FMTitle">Food Management</h1>
                    <button className='add-btn' onClick={() => navigate('/add-food', { state: null })}>Thêm món</button>
                    {error && <p className="text-red-500">{error}</p>}
                    {loading && <p>Loading...</p>}
                    {Object.entries(groupedFoods).map(([type, foods]) => (
                        <div key={type} className="my-4">
                            <h5 onClick={() => toggleFoodList(type)} className="cursor-pointer">
                                {type} ({foods.length}) {isOpen[type] ? '▲' : '▼'}
                            </h5>
                            {!isOpen[type] && (
                                <div className="flex flex-wrap gap-4 text-center">
                                    {foods.map((food) => (
                                        <div key={food._id} className="border p-4 rounded-md">
                                            <img src={food.image} alt={food.name} className="w-full h-32 object-cover" style={{ width: '170px', height: '128px' }} />
                                            <p className="font-semibold">{food.name}</p>
                                            <p className="text-yellow-500 font-bold">{food.price} VND</p>
                                            <button className='Editbtn' onClick={() => navigate(`/edit-food/${food._id}`, { state: food })}>
                                                Edit
                                            </button>
                                            <button className="Deletebtn" onClick={() => handleDeleteClick(food._id)}>Delete</button>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                {isWarningOpen && (
                    <div className='warning'>
                        <div id="alert-additional-content-5" className="p-4 border border-gray-300 rounded-lg bg-gray-50 dark:border-gray-600 dark:bg-gray-800" role="alert">
                            <div className="flex items-center">
                                <svg className="flex-shrink-0 w-4 h-4 me-2 dark:text-gray-300" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
                                    <path d="M10 .5a9.5 9.5 0 1 0 9.5 9.5A9.51 9.51 0 0 0 10 .5ZM9.5 4a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3ZM12 15H8a1 1 0 0 1 0-2h1v-3H8a1 1 0 0 1 0-2h2a1 1 0 0 1 1 1v4h1a1 1 0 0 1 0 2Z" />
                                </svg>
                                <span className="sr-only">Info</span>
                                <h3 className="text-4xl font-normal text-gray-900 dark:text-white">Cảnh báo xóa</h3>
                            </div>
                            <div className="mt-2 mb-4 text-sm text-gray-800 dark:text-gray-300">
                                Bạn có chắc muốn xóa?
                            </div>
                            <div className="flex justify-end">
                                <button type="button" className="text-white bg-red-600 hover:bg-red-700 focus:ring-4 focus:outline-none focus:ring-red-300 font-medium rounded-lg text-xs px-3 py-1.5 me-2 text-center inline-flex items-center dark:bg-red-500 dark:hover:bg-red-400 dark:focus:ring-red-800" onClick={confirmDeleteFood}>
                                    Xóa
                                </button>
                                <button type="button" className="text-gray-800 bg-transparent border border-gray-700 hover:bg-gray-800 hover:text-white focus:ring-4 focus:outline-none focus:ring-gray-300 font-medium rounded-lg text-xs px-3 py-1.5 text-center dark:border-gray-600 dark:hover:bg-gray-600 dark:focus:ring-gray-800 dark:text-gray-300 dark:hover:text-white" onClick={() => setIsWarningOpen(false)}>
                                    Hủy
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>

        </div>
    );
};

export default FoodManager;
