import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Import useParams and useNavigate
import axios from 'axios';

const FoodForm = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState('');
    const [editingFood, setEditingFood] = useState(null);
    const { id } = useParams(); // Get the id from the URL
    const navigate = useNavigate(); // Initialize useNavigate

    // Fetch food item for editing
    const fetchFood = async (id) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/foods/${id}`);
            const food = response.data;
            setName(food.name);
            setPrice(food.price);
            setTypeFood(food.typeFood);
            setImage(food.image);
        } catch (error) {
            console.error('Error fetching food for editing:', error);
        }
    };

    useEffect(() => {
        if (id) {
            fetchFood(id); // Fetch the food item if we're in edit mode
            setEditingFood(true);
        }
    }, [id]);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) {
            formData.append('image', image);
        }

        try {
            if (editingFood) {
                await axios.put(`http://localhost:5000/api/foods/${id}`, formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                });
            } else {
                await axios.post('http://localhost:5000/api/foods', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                });
            }
            navigate('/'); // Redirect back to the food manager after submission
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    return (
        <div>
            <h1>{editingFood ? 'Edit Food' : 'Add Food'}</h1>
            <form onSubmit={handleSubmit} className="mb-4">
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    required
                />
                <input type="file" onChange={handleFileChange} />
                <input
                    type="text"
                    placeholder="Custom TypeFood"
                    value={typeFood}
                    onChange={(e) => setTypeFood(e.target.value)}
                    required
                />
                <button type="submit">{editingFood ? 'Update Food' : 'Add Food'}</button>
            </form>
        </div>
    );
};

export default FoodManagerForm;
