import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FoodManager.css';

const FoodManager = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState('');
    const [typeFoodList, setTypeFoodList] = useState([]);
    const [isCustomType, setIsCustomType] = useState(false);
    const [foods, setFoods] = useState([]);
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isWarningOpen, setIsWarningOpen] = useState(false);
    const [foodToDelete, setFoodToDelete] = useState(null);
    const [isEditMode, setIsEditMode] = useState(false);
    const [isFormVisible, setIsFormVisible] = useState(false);

    // Fetch all food items
    const fetchFoods = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
            const uniqueTypeFoods = [...new Set(response.data.map(food => food.typeFood))];
            setTypeFoodList(uniqueTypeFoods);
        } catch (error) {
            setError('Error fetching foods. Please try again.');
            console.error('Error fetching foods:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) {
            formData.append('image', image);
        } else {
            alert('Chưa thêm ảnh!');
            return;
        }

        const url = isEditMode ? `http://localhost:5000/api/foods/${foodToDelete}` : 'http://localhost:5000/api/foods';
        const method = isEditMode ? 'put' : 'post';

        try {
            const response = await axios({
                method,
                url,
                data: formData,
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            //Cập nhật lại list
            fetchFoods()

            // Xóa các trường trong form
            setName('');
            setPrice('');
            setTypeFood('');
            setImage(null);
            setIsEditMode(false);
            setIsFormVisible(false);
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };


    // Handle file input change
    const handleFileChange = (event) => {
        const file = event.target.files[0];
        console.log(file)
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Chỉ hỗ trợ file .JPG hoặc PNG');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        const grouped = foods.reduce((acc, food) => {
            const type = food.typeFood || 'Others';
            if (!acc[type]) {
                acc[type] = [];
            }
            acc[type].push(food);
            return acc;
        }, {});
        setGroupedFoods(grouped);
    }, [foods]);

    // Confirm deletion of a food item
    const confirmDeleteFood = async () => {
        if (foodToDelete) {
            setLoading(true);
            setError(null);
            try {
                await axios.delete(`http://localhost:5000/api/foods/${foodToDelete}`);
                fetchFoods();
                setIsWarningOpen(false);
            } catch (error) {
                setError('Error deleting food. Please try again.');
                console.error('Error deleting food:', error);
            } finally {
                setLoading(false);
            }
        }
    };

    // Handle delete button click
    const handleDeleteClick = (id) => {
        setFoodToDelete(id);
        setIsWarningOpen(true);
    };

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Handle Edit button click
    const handleEditClick = (food) => {
        setName(food.name);
        setPrice(food.price);
        setTypeFood(food.typeFood);
        setImage(food.image);
        setFoodToDelete(food._id);
        setIsEditMode(true); // Set edit mode
        setIsFormVisible(true); // Show the form
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div>
            <div className='FoodListBG'>
                <div className="px-12">
                    <h1 className="FMTitle">Food Management</h1>
                    <button className='add-btn' onClick={() => {
                        setName('');
                        setPrice('');
                        setTypeFood('');
                        setImage(null);
                        setIsEditMode(false);
                        setIsFormVisible(true);
                    }}>Thêm món</button>
                    {error && <p className="text-red-500">{error}</p>}
                    {loading && <p>Loading...</p>}
                    {Object.entries(groupedFoods).map(([type, foods]) => (
                        <div key={type} className="my-4">
                            <h5 onClick={() => toggleFoodList(type)} className="cursor-pointer">
                                {type} ({foods.length}) {isOpen[type] ? '▲' : '▼'}
                            </h5>
                            {!isOpen[type] && (
                                <div className="flex flex-wrap gap-4 text-center">
                                    {foods.map((food) => (
                                        <div key={food._id} className="flex flex-col justify-between border w-fit pt-4 rounded-md"> {/* Added h-[300px] for card height */}
                                            <div className="flex justify-center items-center mb-2">
                                                <img src={food.image} alt={food.name} className="w-24 h-24 sm:w-32 sm:h-32 object-cover rounded-md" />
                                            </div>
                                            <div className="font-semibold">{food.name}</div>
                                            <div className="text-yellow-500 font-bold">{food.price} VND</div>
                                            <div className="mt-auto"> {/* Added mt-auto to push buttons to the bottom */}
                                                <button className='Editbtn' onClick={() => handleEditClick(food)}>Edit</button>
                                                <button className="Deletebtn" onClick={() => handleDeleteClick(food._id)}>Delete</button>
                                            </div>
                                        </div>
                                    ))}
                                </div>

                            )}
                        </div>
                    ))}
                </div>
            </div>

            {isWarningOpen && (
                <div className='fixed inset-0 flex items-center justify-center z-50'>
                    <div className="bg-white rounded-lg shadow-xl p-6 w-96">
                        <h3 className="text-2xl font-semibold text-red-600 mb-4">Cảnh báo xóa</h3>
                        <p className="text-gray-700 mb-6">Bạn có chắc muốn xóa món ăn này? Hành động này không thể hoàn tác.</p>
                        <div className="flex justify-end space-x-4">
                            <button
                                className="bg-red-600 text-white py-2 px-4 rounded hover:bg-red-700 focus:outline-none"
                                onClick={confirmDeleteFood}
                            >
                                Xóa
                            </button>
                            <button
                                className="bg-gray-200 text-gray-700 py-2 px-4 rounded hover:bg-gray-300 focus:outline-none"
                                onClick={() => setIsWarningOpen(false)}
                            >
                                Hủy
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {isFormVisible && (
                <div className='fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50'>
                    <div className="bg-white rounded-lg shadow-lg p-8 w-full max-w-lg">
                        <h3 className='text-xl font-semibold text-gray-800 mb-4'>{isEditMode ? 'Cập nhập món ăn' : 'Thêm món ăn'}</h3>
                        <form className="space-y-6" onSubmit={handleSubmit}>
                            <div>
                                <label className="block text-gray-700 font-bold mb-1" htmlFor="NameInput">
                                    Tên món ăn
                                </label>
                                <input
                                    className="w-full border-gray-300 border rounded px-3 py-2"
                                    id="NameInput"
                                    type="text"
                                    placeholder="Tên món ăn"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    required
                                />
                            </div>
                            <div>
                                <label className="block text-gray-700 font-bold mb-1" htmlFor="PriceInput">
                                    Giá
                                </label>
                                <input
                                    className="w-full border-gray-300 border rounded px-3 py-2"
                                    id="PriceInput"
                                    type="number"
                                    placeholder="Giá"
                                    value={price}
                                    onChange={(e) => setPrice(e.target.value)}
                                    required
                                />
                            </div>
                            <div>
                                <label className="block text-gray-700 font-bold mb-1" htmlFor="TypeFoodSelect">
                                    Loại món ăn
                                </label>
                                <select
                                    className="w-full border-gray-300 border rounded px-3 py-2"
                                    id="TypeFoodSelect"
                                    value={typeFood}
                                    onChange={(e) => {
                                        setTypeFood(e.target.value);
                                        setIsCustomType(e.target.value === 'Khác');
                                    }}
                                >
                                    <option value="" disabled hidden>--Chọn loại--</option>
                                    {typeFoodList.map((type, index) => (
                                        <option key={index} value={type}>{type}</option>
                                    ))}
                                    <option value="Khác">Khác</option>
                                </select>
                            </div>
                            {isCustomType && (
                                <div>
                                    <label className="block text-gray-700 font-bold mb-1" htmlFor="CustomTypeInput">
                                        Nhập loại khác
                                    </label>
                                    <input
                                        className="w-full border-gray-300 border rounded px-3 py-2"
                                        id="CustomTypeInput"
                                        type="text"
                                        placeholder="Nhập loại khác"
                                        onChange={(e) => setTypeFood(e.target.value)}
                                    />
                                </div>
                            )}
                            <div>
                                <label className="block text-gray-700 font-bold mb-1" htmlFor="ImageUpload">
                                    Ảnh món ăn
                                </label>
                                <input
                                    className="w-full px-3 py-2 text-gray-700"
                                    type="file"
                                    id="ImageUpload"
                                    accept="image/png, image/jpeg"
                                    onChange={handleFileChange}
                                />
                            </div>
                            <div className="flex justify-end gap-4">
                                <button
                                    type="submit"
                                    className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
                                >
                                    {isEditMode ? 'Cập nhập' : 'Thêm'}
                                </button>

                                <button
                                    className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded"
                                    onClick={() => setIsFormVisible(false)}
                                >
                                    Đóng
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

        </div>
    );
};

export default FoodManager;
