import React, { useState, useEffect } from 'react';
import axios from 'axios';
import isEqual from 'lodash/isEqual';
import './FoodManager.css'; // Adjust import according to your style files

const FoodManager = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState('');
    const [typeFoodList, setTypeFoodList] = useState([]);
    const [isCustomType, setIsCustomType] = useState(false);
    const [foods, setFoods] = useState([]);
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isWarningOpen, setIsWarningOpen] = useState(false);
    const [foodToDelete, setFoodToDelete] = useState(null); 
    const [isEditMode, setIsEditMode] = useState(false); // New state variable

    // Fetch all food items
    const fetchFoods = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
            const uniqueTypeFoods = [...new Set(response.data.map(food => food.typeFood))];
            setTypeFoodList(uniqueTypeFoods);
        } catch (error) {
            setError('Error fetching foods. Please try again.');
            console.error('Error fetching foods:', error);
        } finally {
            setLoading(false);
        }
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) {
            formData.append('image', image);
        }
        
        const url = isEditMode ? `http://localhost:5000/api/foods/${foodToDelete}` : 'http://localhost:5000/api/foods';
        const method = isEditMode ? 'put' : 'post';

        try {
            await axios({
                method,
                url,
                data: formData,
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            // Clear the form after submission
            setName('');
            setPrice('');
            setTypeFood('');
            setImage(null);
            setIsEditMode(false); // Reset to add mode after submission
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    // Handle file input change
    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        const grouped = foods.reduce((acc, food) => {
            const type = food.typeFood || 'Others';
            if (!acc[type]) {
                acc[type] = [];
            }
            acc[type].push(food);
            return acc;
        }, {});
        setGroupedFoods(grouped);
    }, [foods]);

    // Confirm deletion of a food item
    const confirmDeleteFood = async () => {
        if (foodToDelete) {
            setLoading(true);
            setError(null);
            try {
                await axios.delete(`http://localhost:5000/api/foods/${foodToDelete}`);
                fetchFoods();
                setIsWarningOpen(false); // Close the warning after deletion
            } catch (error) {
                setError('Error deleting food. Please try again.');
                console.error('Error deleting food:', error);
            } finally {
                setLoading(false);
            }
        }
    };

    // Handle delete button click
    const handleDeleteClick = (id) => {
        setFoodToDelete(id);
        setIsWarningOpen(true);
    };

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Handle Edit button click
    const handleEditClick = (food) => {
        setName(food.name);
        setPrice(food.price);
        setTypeFood(food.typeFood);
        setImage(food.image);
        setFoodToDelete(food._id);
        setIsEditMode(true); // Set edit mode
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div>
            <div className='FoodListBG'>
                <div className="px-12">
                    <h1 className="FMTitle">Food Management</h1>
                    <button className='add-btn' onClick={() => { 
                        setName(''); 
                        setPrice(''); 
                        setTypeFood(''); 
                        setImage(null); 
                        setIsEditMode(false); // Reset to add mode
                    }}>Thêm món</button>
                    {error && <p className="text-red-500">{error}</p>}
                    {loading && <p>Loading...</p>}
                    {Object.entries(groupedFoods).map(([type, foods]) => (
                        <div key={type} className="my-4">
                            <h5 onClick={() => toggleFoodList(type)} className="cursor-pointer">
                                {type} ({foods.length}) {isOpen[type] ? '▲' : '▼'}
                            </h5>
                            {!isOpen[type] && (
                                <div className="flex flex-wrap gap-4 text-center">
                                    {foods.map((food) => (
                                        <div key={food._id} className="border p-4 rounded-md">
                                            <img src={food.image} alt={food.name} className="w-full h-32 object-cover" style={{ width: '170px', height: '128px' }} />
                                            <p className="font-semibold">{food.name}</p>
                                            <p className="text-yellow-500 font-bold">{food.price} VND</p>
                                            <button className='Editbtn' onClick={() => handleEditClick(food)}>Edit</button>
                                            <button className="Deletebtn" onClick={() => handleDeleteClick(food._id)}>Delete</button>
                                        </div>
                                    ))} 
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                {isWarningOpen && (
                    <div className='warning'>
                        <div id="alert-additional-content-5" className="p-4 border border-gray-300 rounded-lg bg-gray-50" role="alert">
                            <div className="flex items-center">
                                <h3 className="text-4xl font-normal text-gray-900">Cảnh báo xóa</h3>
                            </div>
                            <div className="mt-2 mb-4 text-sm text-gray-800">
                                Bạn có chắc muốn xóa?
                            </div>
                            <div className="flex justify-end">
                                <button type="button" className="text-white bg-red-600 hover:bg-red-700" onClick={confirmDeleteFood}>
                                    Xóa
                                </button>
                                <button type="button" className="text-gray-800 bg-transparent border border-gray-700 hover:bg-gray-800 hover:text-white" onClick={() => setIsWarningOpen(false)}>
                                    Hủy
                                </button>
                            </div>
                        </div>
                    </div>
                )}

                <div className='edit-add-form items-center'>
                    <h1 className='Formtitle'>{isEditMode ? 'Cập nhập món ăn' : 'Thêm món ăn'}</h1>
                    <div className="Formbody flex">
                        <form className="w-full max-w-sm" onSubmit={handleSubmit}>
                            <div className="md:flex md:items-center mb-6">
                                <div className="md:w-1/3">
                                    <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="NameInput">
                                        Tên món ăn
                                    </label>
                                </div>
                                <div className="md:w-2/3">
                                    <input className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                        id="NameInput"
                                        type="text"
                                        placeholder="Name"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                        required
                                    />
                                </div>
                            </div>
                            <div className="md:flex md:items-center mb-6">
                                <div className="md:w-1/3">
                                    <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="PriceInput">
                                        Giá
                                    </label>
                                </div>
                                <div className="md:w-2/3">
                                    <input className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                        id="PriceInput"
                                        type="number"
                                        placeholder="Price"
                                        value={price}
                                        onChange={(e) => setPrice(e.target.value)}
                                        required
                                    />
                                </div>
                            </div>
                            <div className="md:flex md:items-center mb-6">
                                <div className="md:w-1/3">
                                    <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="TypeFoodSelect">
                                        Loại món ăn
                                    </label>
                                </div>
                                <div className="md:w-2/3">
                                    <select id="TypeFoodSelect" className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                        value={typeFood}
                                        onChange={(e) => {
                                            setTypeFood(e.target.value);
                                            setIsCustomType(e.target.value === 'Khác');
                                        }}>
                                        <option value="" disabled hidden>--Chọn loại--</option>
                                        {typeFoodList.map((type, index) => (
                                            <option key={index} value={type}>{type}</option>
                                        ))}
                                        <option value="Khác">Khác</option>
                                    </select>
                                </div>
                            </div>
                            {isCustomType && (
                                <div className="md:flex md:items-center mb-6">
                                    <div className="md:w-1/3">
                                        <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="CustomTypeInput">
                                            Nhập loại khác
                                        </label>
                                    </div>
                                    <div className="md:w-2/3">
                                        <input className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                            id="CustomTypeInput"
                                            type="text"
                                            placeholder="Nhập loại khác"
                                            value={typeFood}
                                            onChange={(e) => setTypeFood(e.target.value)}
                                        />
                                    </div>
                                </div>
                            )}
                            <div className="md:flex md:items-center mb-6">
                                <div className="md:w-1/3">
                                    <label className="block text-gray-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="ImageUpload">
                                        Ảnh món ăn
                                    </label>
                                </div>
                                <div className="md:w-2/3">
                                    <input type="file" id="ImageUpload" accept="image/png, image/jpeg" onChange={handleFileChange} />
                                </div>
                            </div>
                            <div className="md:flex md:items-center">
                                <div className="md:w-1/3" />
                                <div className="md:w-2/3">
                                    <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                                        {isEditMode ? 'Cập nhập món ăn' : 'Thêm món ăn'}
                                    </button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default FoodManager;
