import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import FoodManagerForm from '../../componets/FoodManagerForm/FoodManagerForm';
import './FoodManager.css';

const FoodManager = () => {
    const history = useHistory();
    const [foods, setFoods] = useState([]);
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState('');
    const [editingFood, setEditingFood] = useState(null);
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});

    const [headerHeight, setHeaderHeight] = useState(0);
    const [navHeight, setNavHeight] = useState(0);
    const [showForm, setShowForm] = useState(false);

    // Fetch all food items
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        const grouped = foods.reduce((acc, food) => {
            const type = food.typeFood;
            if (!acc[type]) {
                acc[type] = [];
            }
            acc[type].push(food);
            return acc;
        }, {});
        setGroupedFoods(grouped);
    }, [foods]);

    // Create a new food item
    const createFood = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (!image) {
            alert('Vui lòng nhập file');
            return;
        }
        formData.append('image', image);

        try {
            await axios.post('http://localhost:5000/api/foods', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            fetchFoods(); // Refresh the list
            resetForm();
            setShowForm(false); // Hide the form after adding
        } catch (error) {
            console.error('Error creating food:', error);
        }
    };

    const handleAddNewFood = () => {
        resetForm();
        history.push('/food-manager-form'); // Navigate to the form page
    };

    const handleEdit = (food) => {
        setEditingFood(food);
        setName(food.name);
        setPrice(food.price);
        setTypeFood(food.typeFood);
        history.push('/food-manager-form'); // Navigate to the form page
    };

    // Update a food item
    const updateFood = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) {
            formData.append('image', image);
        }

        try {
            await axios.put(`http://localhost:5000/api/foods/${editingFood._id}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            fetchFoods(); // Refresh the list
            resetForm();
            setShowForm(false); // Hide the form after updating
        } catch (error) {
            console.error('Error updating food:', error);
        }
    };

    // Delete a food item
    const deleteFood = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/foods/${id}`);
            fetchFoods(); // Refresh the list
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Scroll to section
    const scrollToSection = (type) => {
        const element = document.getElementById(type);
        if (element) {
            let offsetPosition = element.getBoundingClientRect().top + window.scrollY - (headerHeight + navHeight);
            window.scrollTo({
                top: offsetPosition,
                behavior: "smooth"
            });
        }
    };

    // Reset form fields
    const resetForm = () => {
        setEditingFood(null);
        setName('');
        setPrice('');
        setTypeFood('');
        setImage(null);
    };

    // Handle scroll event
    useEffect(() => {
        const headerElement = document.querySelector("nav");
        if (headerElement) {
            setHeaderHeight(headerElement.offsetHeight);
            setNavHeight(headerElement.offsetHeight);
        }

        const handleScroll = () => {
            setNavScrolled(window.scrollY > headerHeight);
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, [headerHeight]);

    // Fetch foods on component mount
    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div className="px-12">
            <h1>Food Management</h1>
            <button onClick={() => { setShowForm(true); resetForm(); }}>Add New Food</button>
            {showForm && (
                <FoodManagerForm
                    name={name}
                    price={price}
                    image={image}
                    typeFood={typeFood}
                    setName={setName}
                    setPrice={setPrice}
                    handleFileChange={handleFileChange}
                    setTypeFood={setTypeFood}
                    editingFood={editingFood}
                    onSubmit={editingFood ? updateFood : createFood}
                />
            )}
            {Object.entries(groupedFoods).map(([type, foods]) => (
                <div key={type} className="my-4" id={type}>
                    <h5 className="mb-3 flex items-center justify-between cursor-pointer" onClick={() => toggleFoodList(type)}>
                        <span className="text-lg font-semibold">{type}</span>
                        <div className="border border-dashed h-px grow mx-2"></div>
                        {isOpen[type] ? '▲' : '▼'}
                    </h5>
                    <div className={`transition-all duration-300 ${isOpen[type] ? '' : 'hidden'}`}>
                        <div className="flex flex-wrap gap-4">
                            {foods.map((food) => (
                                <div key={food._id} className="text-center border p-4 rounded-md shadow-sm" style={{ width: "160px", height: "250px" }}>
                                    {food.image && (
                                        <img
                                            src={food.image}
                                            className="w-full h-32 object-cover mb-2"
                                            style={{ borderRadius: "8px" }}
                                            alt={food.name}
                                        />
                                    )}
                                    <p className="font-semibold">{food.name}</p>
                                    <p className="text-yellow-500 font-bold">{food.price} VND</p>
                                    <button onClick={() => handleEdit(food)}>Edit</button>
                                    <button onClick={() => deleteFood(food._id)}>Delete</button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default FoodManager;
