import { useEffect, useState } from "react";
import axios from 'axios';
import FoodList from "../../componets/FoodList/FoodList";

const Home = () => {
    const [foods, setFoods] = useState([]);
    
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            // Group food items by typeFood
            const grouped = response.data.reduce((acc, food) => {
                const type = food.typeFood; // Assuming each food has a typeFood property
                if (!acc[type]) {
                    acc[type] = [];
                }
                acc[type].push(food);
                return acc;
            }, {});
            setGroupedFoods(grouped);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div className="container">
            <FoodList/>
        </div>
    );
};

export default Home;
