import { useEffect, useState } from "react";
import axios from 'axios';

const Home = () => {
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({}); // Theo dõi trạng thái mở của từng nhóm

    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            // Nhóm thực phẩm theo typeFood
            const grouped = response.data.reduce((acc, food) => {
                const type = food.typeFood; // Giả sử mỗi food có thuộc tính typeFood
                if (!acc[type]) {
                    acc[type] = [];
                }
                acc[type].push(food);
                return acc;
            }, {});
            setGroupedFoods(grouped);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div className="row">
            {Object.entries(groupedFoods).map(([type, foods]) => (
                <div key={type} className="col-sm-6 col-lg-4">
                    <h5 className="mb-3" onClick={() => toggleFoodList(type)} style={{ cursor: 'pointer' }}>
                        {type} {isOpen[type] ? '▲' : '▼'}
                    </h5>
                    <div className={`${isOpen[type] ? '' : 'collapse'}`}>
                        {/* Sử dụng Flexbox cho danh sách thực phẩm */}
                        <div className="d-flex flex-column align-items-center">
                            {foods.map((food) => (
                                <div key={food._id} className="text-center mx-2 mb-3" style={{ width: '100%' }}>
                                    <div className="card border-0">
                                        {food.image && (
                                            <img src={food.image} className="card-img-top" alt={food.name} width="100" height="150" />
                                        )}
                                        <div className="card-body p-2">
                                            <h6 className="card-title text-uppercase">{food.name}</h6>
                                            <p className="text-warning font-weight-bold">{food.price} VND</p>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Home;
