import { useEffect, useState } from "react";
import axios from 'axios';

const Home = () => {
    const [foods, setFoods] = useState([]);
    const [isOpen, setIsOpen] = useState(true);

    const toggleFoodList = () => {
        setIsOpen(!isOpen);
    };

    // Fetch all food items
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
            console.log(foods)
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div className="row">
            <div className="col-sm-6 col-lg-4">
                                {/* Title acts as a dropdown button */}
                                <h5 className="mb-3" onClick={toggleFoodList} style={{ cursor: 'pointer' }}>
                    ĐỒ UỐNG ĐÓNG CHAI {isOpen ? '▲' : '▼'}
                </h5>

                {/* Conditional rendering of the food list */}
                <div className={`collapse ${isOpen ? 'show' : ''}`}>
                    <div className="d-flex justify-content-between">
                        {foods.map((food) => (
                            <div key={food._id} className="text-center mx-2">
                                <div className="card border-0">
                                    {food.image && (
                                        <img src={food.image} className="card-img-top" alt={food.name} width="100" height="150" />
                                    )}
                                    <div className="card-body p-2">
                                        <h6 className="card-title text-uppercase">{food.name}</h6>
                                        <p className="text-warning font-weight-bold">{food.price} VND</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
