import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FoodManager.css';

const FoodManager = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState('');
    const [typeFoodList, setTypeFoodList] = useState([]);
    const [isCustomType, setIsCustomType] = useState(false);
    const [foods, setFoods] = useState([]);
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isWarningOpen, setIsWarningOpen] = useState(false);
    const [foodToDelete, setFoodToDelete] = useState(null);
    const [isEditMode, setIsEditMode] = useState(false);
    const [isFormVisible, setIsFormVisible] = useState(false); // State to control form visibility

    // Fetch all food items
    const fetchFoods = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
            const uniqueTypeFoods = [...new Set(response.data.map(food => food.typeFood))];
            setTypeFoodList(uniqueTypeFoods);
        } catch (error) {
            setError('Error fetching foods. Please try again.');
            console.error('Error fetching foods:', error);
        } finally {
            setLoading(false);
        }
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) {
            formData.append('image', image);
        }

        const url = isEditMode ? `http://localhost:5000/api/foods/${foodToDelete}` : 'http://localhost:5000/api/foods';
        const method = isEditMode ? 'put' : 'post';

        try {
            await axios({
                method,
                url,
                data: formData,
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            // Clear the form after submission
            setName('');
            setPrice('');
            setTypeFood('');
            setImage(null);
            setIsEditMode(false); // Reset to add mode after submission
            setIsFormVisible(false); // Hide form after submission
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    // Handle file input change
    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        const grouped = foods.reduce((acc, food) => {
            const type = food.typeFood || 'Others';
            if (!acc[type]) {
                acc[type] = [];
            }
            acc[type].push(food);
            return acc;
        }, {});
        setGroupedFoods(grouped);
    }, [foods]);

    // Confirm deletion of a food item
    const confirmDeleteFood = async () => {
        if (foodToDelete) {
            setLoading(true);
            setError(null);
            try {
                await axios.delete(`http://localhost:5000/api/foods/${foodToDelete}`);
                fetchFoods();
                setIsWarningOpen(false); // Close the warning after deletion
            } catch (error) {
                setError('Error deleting food. Please try again.');
                console.error('Error deleting food:', error);
            } finally {
                setLoading(false);
            }
        }
    };

    // Handle delete button click
    const handleDeleteClick = (id) => {
        setFoodToDelete(id);
        setIsWarningOpen(true);
    };

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Handle Edit button click
    const handleEditClick = (food) => {
        setName(food.name);
        setPrice(food.price);
        setTypeFood(food.typeFood);
        setImage(food.image);
        setFoodToDelete(food._id);
        setIsEditMode(true); // Set edit mode
        setIsFormVisible(true); // Show the form
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div>
            <div className='FoodListBG'>
                <div className="px-12">
                    <h1 className="FMTitle">Food Management</h1>
                    <button className='add-btn' onClick={() => {
                        setName('');
                        setPrice('');
                        setTypeFood('');
                        setImage(null);
                        setIsEditMode(false); // Reset to add mode
                        setIsFormVisible(true); // Show the form when adding
                    }}>Thêm món</button>
                    {error && <p className="text-red-500">{error}</p>}
                    {loading && <p>Loading...</p>}
                    {Object.entries(groupedFoods).map(([type, foods]) => (
                        <div key={type} className="my-4">
                            <h5 onClick={() => toggleFoodList(type)} className="cursor-pointer">
                                {type} ({foods.length}) {isOpen[type] ? '▲' : '▼'}
                            </h5>
                            {!isOpen[type] && (
                                <div className="flex flex-wrap gap-4 text-center">
                                    {foods.map((food) => (
                                        <div key={food._id} className="border p-4 rounded-md">
                                            <img src={food.image} alt={food.name} className="w-full h-32 object-cover" style={{ width: '170px', height: '128px' }} />
                                            <p className="font-semibold">{food.name}</p>
                                            <p className="text-yellow-500 font-bold">{food.price} VND</p>
                                            <button className='Editbtn' onClick={() => handleEditClick(food)}>Edit</button>
                                            <button className="Deletebtn" onClick={() => handleDeleteClick(food._id)}>Delete</button>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            {isWarningOpen && (
                <div className='fixed inset-0 flex items-center justify-center z-50'>
                    <div className="bg-white rounded-lg shadow-xl p-6 w-96">
                        <h3 className="text-2xl font-semibold text-red-600 mb-4">Cảnh báo xóa</h3>
                        <p className="text-gray-700 mb-6">Bạn có chắc muốn xóa món ăn này? Hành động này không thể hoàn tác.</p>
                        <div className="flex justify-end space-x-4">
                            <button
                                className="bg-red-600 text-white py-2 px-4 rounded hover:bg-red-700 focus:outline-none"
                                onClick={confirmDeleteFood}
                            >
                                Xóa
                            </button>
                            <button
                                className="bg-gray-200 text-gray-700 py-2 px-4 rounded hover:bg-gray-300 focus:outline-none"
                                onClick={() => setIsWarningOpen(false)}
                            >
                                Hủy
                            </button>
                        </div>
                    </div>
                </div>
            )}
                <div>
                    <div>
                        
                    </div>
                </div>
            
            {isFormVisible && (
                <div></div>
            )}

        </div>
    );
};

export default FoodManager;
