import React, { useEffect } from 'react';

const FoodManagerForm = ({ name, price, image, typeFood, setName, setPrice, handleFileChange, setTypeFood, onSubmit }) => {
    useEffect(() => {
    }, [name, price, typeFood]); // Depend on props if needed

    return (
        <form onSubmit={onSubmit} className="mb-4">
            <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
            />
            <input type="file" onChange={handleFileChange} />
            <input
                type="text"
                placeholder="Custom TypeFood"
                value={typeFood}
                onChange={(e) => setTypeFood(e.target.value)}
                required
            />
            <button type="submit">{name ? 'Update Food' : 'Add Food'}</button>
        </form>
    );
};

export default FoodManagerForm;
