import React, { useEffect } from 'react';

const FoodManagerForm = ({ editingFood, onSubmit, onCancel }) => {
    const [name, setName] = useState(editingFood ? editingFood.name : '');
    const [price, setPrice] = useState(editingFood ? editingFood.price : '');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState(editingFood ? editingFood.typeFood : '');

    useEffect(() => {
        if (editingFood) {
            setName(editingFood.name);
            setPrice(editingFood.price);
            setTypeFood(editingFood.typeFood);
        } else {
            resetForm();
        }
    }, [editingFood]);

    const resetForm = () => {
        setName('');
        setPrice('');
        setImage(null);
        setTypeFood('');
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) formData.append('image', image);
        onSubmit(formData); // Submit the form data
    };

    return (
        <form onSubmit={handleSubmit} className="mb-4">
            <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
            />
            <input type="file" onChange={handleFileChange} required />
            <input
                type="text"
                placeholder="Custom TypeFood"
                value={typeFood}
                onChange={(e) => setTypeFood(e.target.value)}
                required
            />
            <button type="submit">{editingFood ? 'Update Food' : 'Add Food'}</button>
            <button type="button" onClick={onCancel}>Cancel</button>
        </form>
    );
};

export default FoodManagerForm;
