import { useEffect, useState, useRef } from "react";

const FoodList = ({ foods }) => {
    const [groupedFoods, setGroupedFoods] = useState({});
    const [isOpen, setIsOpen] = useState({});
    const [navSticky, setNavSticky] = useState(false);
    const navRef = useRef(null); // Reference for the nav element

    // Toggle food list display
    const toggleFoodList = (type) => {
        setIsOpen((prevState) => ({ ...prevState, [type]: !prevState[type] }));
    };

    // Group food items by their category/typeFood
    useEffect(() => {
        if (foods.length > 0) {
            const grouped = foods.reduce((acc, food) => {
                const type = food.typeFood; // Assuming each food has a typeFood property
                if (!acc[type]) {
                    acc[type] = [];
                }
                acc[type].push(food);
                return acc;
            }, {});
            setGroupedFoods(grouped);
        }
    }, [foods]);

    // Handle scroll event to make nav sticky when scrolling past it
    useEffect(() => {
        const handleScroll = () => {
            const navTop = navRef.current.getBoundingClientRect().top; // Distance from the top of the page

            if (window.scrollY >= navTop + window.scrollY) {
                setNavSticky(true); // Make the nav sticky when the user scrolls past it
            } else {
                setNavSticky(false); // Reset nav position when back at the top
            }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    return (
        <div className="px-12">
            {/* Header area (simulating a header) */}
            <header className="bg-gray-100 p-4 mb-4">
                <h1>My Food List</h1>
            </header>

            {/* Navigation bar for quick jump to categories */}
            <nav
                ref={navRef}
                className={`mb-4 p-4 transition-all duration-300 z-10 ${navSticky ? 'fixed top-16 bg-blue-500 text-white shadow-lg w-full' : 'relative bg-transparent text-black'}`}
            >
                <ul className="flex space-x-4">
                    {Object.keys(groupedFoods).map((type) => (
                        <li key={type}>
                            <a href={`#${type}`} className="hover:underline cursor-pointer">
                                {type}
                            </a>
                        </li>
                    ))}
                </ul>
            </nav>

            {/* Spacer to ensure content doesn't overlap the sticky nav */}
            <div className={`${navSticky ? 'h-16' : ''}`}></div>

            {/* Food categories and items */}
            {Object.entries(groupedFoods).map(([type, foods]) => (
                <div key={type} className="my-4" id={type}>
                    {/* Category header with toggle functionality */}
                    <h5 className="mb-3 flex items-center justify-between cursor-pointer" onClick={() => toggleFoodList(type)}>
                        <span className="text-lg font-semibold">{type}</span>
                        <div className="border border-dashed h-px grow mx-2"></div>
                        {isOpen[type] ? '▲' : '▼'}
                    </h5>
                    {/* Food items under each category */}
                    <div className={`transition-all duration-300 ${isOpen[type] ? '' : 'hidden'}`}>
                        <div className="grid grid-cols-3 gap-4">
                            {foods.map((food) => (
                                <div key={food._id} className="text-center border p-4 rounded-md shadow-sm" style={{ width: "160px", height: "250px" }}>
                                    {food.image && (
                                        <img
                                            src={food.image}
                                            className="w-full h-32 object-cover mb-2"
                                            style={{ borderRadius: "8px" }}  // Added border-radius for rounded corners
                                            alt={food.name}
                                        />
                                    )}
                                    <p className="font-semibold">{food.name}</p>
                                    <p className="text-yellow-500 font-bold">{food.price} VND</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default FoodList;
