import { useEffect, useState } from "react";
import axios from 'axios';
import FoodList from "../../componets/FoodList";

const Home = () => {
    const [foods, setFoods] = useState([]);

    // Fetch all food items
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    useEffect(() => {
        fetchFoods();
    }, []);

    return (
        <div className="row">
            <div className="col-sm-6 col-lg-4">
                <ul className="list-unstyled row">
                    {foods.map((food) => (
                        <li key={food._id} className="col-12 col-md-4 mb-4">
                            #Drop
                            <div className="card card-couser-item">
                                {food.image && (
                                    <img src={food.image} className="card-img-top" alt={food.name} width="150" height="160"/>
                                )}
                                <div className="card-body">
                                    <h5 className="card-title underline-none">{food.name}</h5>
                                    <p className="description card-text">{food.price}</p>
                                    <a href="/Foods/manager" className="btn btn-primary">Đăng ký</a>
                                </div>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Home;
