import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const FoodManagerForm = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const foodData = location.state || {};
    console.log(foodData)
    const [name, setName] = useState(foodData ? foodData.name : '');
    const [price, setPrice] = useState(foodData ? foodData.price : '');
    const [image, setImage] = useState(null);
    const [typeFood, setTypeFood] = useState(foodData ? foodData.typeFood : '');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', price);
        formData.append('typeFood', typeFood);
        if (image) {
            formData.append('image', image);
        }

        const url = foodData ? `http://localhost:5000/api/foods/${foodData._id}` : 'http://localhost:5000/api/foods';
        const method = foodData ? 'put' : 'post';

        try {
            await axios({
                method,
                url,
                data: formData,
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            navigate('/Foods/manager'); // Redirect back to FoodManager
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const fileType = file.type;
            if (fileType !== 'image/jpeg' && fileType !== 'image/png') {
                alert('Only JPG and PNG files are supported!');
                setImage(null);
                return;
            }
            setImage(file);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="mb-4">
            <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
            />
            <input type="file" onChange={handleFileChange} />
            <input
                type="text"
                placeholder="Custom TypeFood"
                value={typeFood}
                onChange={(e) => setTypeFood(e.target.value)}
                required
            />
            <button type="submit">{foodData ? 'Update Food' : 'Add Food'}</button>
        </form>
    );
};

export default FoodManagerForm;
