import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './FoodManager.css';

const FoodManager = () => {
    const [foods, setFoods] = useState([]);
    const navigate = useNavigate(); // Initialize the navigate hook

    // Fetch all food items
    const fetchFoods = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/foods');
            setFoods(response.data);
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    // Delete a food item
    const deleteFood = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/foods/${id}`);
            fetchFoods(); // Refresh the list after deletion
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    // Handle edit
    const handleEdit = (food) => {
        navigate(`/edit-food/${food._id}`, { state: food }); // Navigate to the edit route
    };

    useEffect(() => {
        fetchFoods(); // Fetch foods on component mount
    }, []);

    return (
        <div className="px-12">
            <h1 className="FMTitle">Food Management</h1>
            <button onClick={() => navigate('/add-food')}>Add New Food</button>
            {/* Render food items here */}
            <div>
                {foods.map((food) => (
                    <div key={food._id}>
                        <h5>{food.name}</h5>
                        <button onClick={() => handleEdit(food)}>Edit</button>
                        <button onClick={() => deleteFood(food._id)}>Delete</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FoodManager;
